import React,{useState, useEffect} from "react";

// import { BsHouseDoorFill } from "react-icons/bs";
//  import { BsFillInfoCircleFill } from "react-icons/bs";
// import { BsCardText } from "react-icons/bs";
//  import { FaHandshake } from "react-icons/fa";
//  import { FaHands } from "react-icons/fa";
//  import { BsLockFill } from "react-icons/bs";
import { AiOutlineBars } from "react-icons/ai";
import Connexion from "./Connexion";
import {BrowserRouter, Route, Link} from 'react-router-dom'

import "../Css//App.css"

const [toggleMenu, setToggleMenu] = useState (false);
    const [largeur, setLargeur] = useState (window.innerWidth)

    const toggleNavSmallScreen = () => {
        setToggleMenu(!toggleMenu);
    }

    useEffect(() => {
    
        const changeWidth = () => {
            setLargeur(window.innerWidth);

           if(window.innerWidth > 825){
             setToggleMenu(false);
            }
            // if(window.innerHeight > 825){
            //     setToggleMenu(false);
            //    }
        }
        window.addEventListener('resize', changeWidth);
        return() => {
            window.removeEventListener('resize', changeWidth);

        }

    }, [])
function Nav(){
  
    return(
        <BrowserRouter>
        <div>
        <nav>
            {(toggleMenu || largeur > 825) && (
                <ul className="liste">
                   <li className="items"> Accueil </li>
                   <li className="items"> Qui sommes nous</li>
                   <li className="items"> Actualités</li>
                   <li className="items"> Nos action</li>
                   <li className="items"> Espace bénévoles</li>
                   <li className="items">
                       <Link  className="nav-link text-light" to="/Connexion">Connexion</Link>
                    </li>
                </ul>
            )}
            <button onClick={toggleNavSmallScreen} className="bgm"><AiOutlineBars /></button>
        </nav>
        <Route path="/Connexion" component={Connexion} />
        </div>
        </BrowserRouter>
        
    );

}

export default Nav;
