import React, {Component} from 'react';
import "../Connexion.css"

class Connexion extends Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.hc = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  render() {
    return (
      <form>
        <div className='Connex'>
          <h1 name="TitreConnexion">Connexion</h1>
          <label>Login : </label><br></br>
          <input type="text" class="emailText" onChange={this.hc} /><br></br>
          <label>Mot de passe : </label><br></br>
          <input type="text" class="MdpText" onChange={this.hc} /><br></br>
          <input type="submit" value="Se connecter" /> <br></br>
          <p>Mot de passe oubli√© ?</p>
          <hr></hr>
          <h1 name="TitreConnexion">Nouveau Utilisateur</h1>
          <input id="btnInscription" type="submit" value="S'inscrire" onClick="document.location.href='Inscription.js'"/>
          </div>
      </form>
    );
  }
}

export default Connexion;
